////****** SET PROBLEM INPUTS ******//
//// number of vertices
#define NUM_V 50
//// r-neighbour bootstrap percolation value
#define R_VAL 2
//// number of steps the entire set needs to percolate
//// set = NUM_V if you want the general problem solution
#define K_VAL 5
//// !!!!!
//// DO NOT FORGET TO ADD ADJACENCY MATRIX INFO ON ROW 138
//// !!!!!
////****** SET PROBLEM INPUTS ******//

//****** RETURN DEGREE OF VERTEX + 1 ******//
//
int Vertex_degree(bool adj_matrix[NUM_V][NUM_V], int v) {
    int degree = 0;
    int u;
    for (u = 0; u < NUM_V; u++) {
        if (u != v) {
            degree += adj_matrix[v][u];
        }
    }
    return degree + 1;
};
//
//****** RETURN DEGREE OF VERTEX + 1 ******//

//****** CONVERT INTEGER TO STRING ******//
//
string itos(int i) {stringstream s; s << i; return s.str(); }
//
//****** CONVERT INTEGER TO STRING ******//

//****** LP TOOL ******//
//
bool LP(bool adj_matrix[NUM_V][NUM_V], GRBEnv grb_env) {
    int v, u, i;

    // create an empty model
    GRBModel model = GRBModel(grb_env);
    // define a 2-dim array for the variables
    //              { 1 if vertex v is infected at step i
    // vars[v][i] = { and
    //              { 0 otherwise
    GRBVar vars[NUM_V][K_VAL + 1];
    // make variables
    for (v = 0; v < NUM_V; v++) {
        // lower bound, upper bound, objective coeff, type
        string s = "init set vertex " + itos(v);
        vars[v][0] = model.addVar(0, 1, 1, 'B', s);
        for (i = 1; i <= K_VAL; i++) {
            // lower bound, upper bound, objective coeff, type
            vars[v][i] = model.addVar(0, 1, 0, 'B');
        }
    }

    // CONSTRAINT 3
    // All vertices must be infected by step k
    GRBLinExpr con3 = 0;
    
    for (v = 0; v < NUM_V; v++) {

        con3 += vars[v][K_VAL];
        for (i = 1; i <= K_VAL; i++) {

            // CONSTRAINT 1
            // v cannot be infected at step i unless it is already infected
            // or has r neighbors who are infected
            GRBLinExpr cons1 = R_VAL*vars[v][i-1] - R_VAL*vars[v][i];

            // CONSTRAINT 2
            // v must be infected at step i if it is already infected or
            // has r neighbors who are infected.
            GRBLinExpr cons2 = R_VAL*vars[v][i-1] - R_VAL + 1 - Vertex_degree(adj_matrix, v)*R_VAL*vars[v][i];

            for (u = 0; u < NUM_V; u++) {
                // open neighborhood, not closed
                if (u != v) {
                    if (adj_matrix[v][u]) {
                        cons1 += vars[u][i-1];
                        cons2 += vars[u][i-1];
                    }
                }
            }
            // add the constraint 1 and 2
            model.addConstr(cons1 >= 0);
            // add the constraint 1 and 2
            model.addConstr(cons2 <= 0);
        }
    }
    // add the constraint 3
    model.addConstr(con3 == NUM_V);

    // The objective is to minimize size of percolating set
    model.set(GRB_IntAttr_ModelSense, GRB_MINIMIZE);

    // The objective coefficients are set during the creation of
    // the decision variables above. Run model
    model.optimize();

    // SOLUTION
    printf("Min size of %d-neighbour perc set in %d steps: %f\n", int(R_VAL), int(K_VAL), model.get(GRB_DoubleAttr_ObjVal));
    printf("%d-neighbour perc set: ", int(R_VAL));
    for (v = 0; v < NUM_V; v++) {
        if (vars[v][0].get(GRB_DoubleAttr_X)) {
            printf("%d ", v);
        }
    }
    printf("\n");
//    for (i = 1; i <= K_VAL; i++) {
//        printf("vertices infected at step %d: ", i);
//        for (v = 0; v < NUM_V; v++) {
//            if (int(vars[v][i].get(GRB_DoubleAttr_X))) {
//                printf("%d ", v);
//            }
//        }
//        printf("\n");
//    }

    return 0;
};
